% Assignment #6, Per Jesper Sjšstršm% 2/27/00, The I&F neuron%%% InitV_rest = -70e-3;				% Resting membrane potential [V]R_m = 10e6;						% Input resistance of the cell [Ohm]tau_m = 10e-3;					% Membrane time constant [s]V_th = -54e-3;					% Threshold for spiking [V]V_reset = -80e-3;				% Reset value, after a spike [V]V_max = 40e-3;					% Size of a spike [V]I_e = 1.8e-9;					% Injected current [A]V = V_rest;						% Initiate the membrane potential to the leakage reversal [V]t_max = 0.5;					% [s]dt = 0.0001;					% [s]t_vec = 0:dt:t_max;V_vec = zeros(1,length(t_vec));	% [V]t_low = 0.1;					% Used for the current stept_hi = 0.4;I_e_max = 3e-9;					% Used for the sinusoidal current injectionsine_f = 0.100;%%% Loopfor i = 1:length(t_vec),	V_vec(i) = V;				% Store away the membrane voltage		% Produce current step	if (t_vec(i)>t_low) & (t_vec(i)<t_hi),		I_e = 2e-9;	else		I_e = 0;	end;	% Produce sinusoidal current% 	I_e = I_e_max*sin(2*pi*t_vec(i)/sine_f);	if (V==V_max)				% Previous step was a spike?		V = V_reset;			% If so, do a reset	end;	V = V_rest + R_m*I_e + (V - V_rest - R_m*I_e )*exp(-dt/tau_m);	if (V>V_th),				% Above threshold?		V = V_max;				% If so, produce the spike	end;end;%%% Plotfigure(1);clforient tall;subplot(1,1,1);plot(t_vec,V_vec);xlabel('time [s]');ylabel('ampl [V]');